import React, { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from './App';

const Home = () => {
  const [videos, setVideos] = useState([]);
  const [country, setCountry] = useState('US');
  const [authChecked, setAuthChecked] = useState(false);
  const [ratings, setRatings] = useState({}); // <== нове
  const navigate = useNavigate();
  const { user, setUser } = useContext(AuthContext);

  useEffect(() => {
    fetch("http://16.171.149.32.nip.io:4000/auth/user", { credentials: "include" })
      .then(res => {
        if (res.status === 401) {
          setUser(null);
          navigate("/login");
          return null;
        }
        return res.json();
      })
      .then(data => {
        if (data) setUser(data);
        setAuthChecked(true);
      })
      .catch(err => {
        console.error("Auth check error:", err);
        setUser(null);
        navigate("/login");
      });
  }, [navigate, setUser]);

  useEffect(() => {
    if (!authChecked) return;

    fetch(`http://16.171.149.32.nip.io:4000/api/videos?country=${country}`, {
      credentials: "include"
    })
      .then(res => res.json())
      .then(data => {
        setVideos(data);

        // Після отримання відео — отримати рейтинги
        const fetchRatings = async () => {
          const ratingMap = {};
          for (const video of data) {
            try {
              const res = await fetch(`http://16.171.149.32.nip.io:4000/api/rating/${video.videoId}`);
              const result = await res.json();
              ratingMap[video.videoId] = result.average || null;
            } catch (err) {
              console.error("Rating fetch error:", err);
            }
          }
          setRatings(ratingMap);
        };

        fetchRatings();
      })
      .catch(err => {
        console.error("Failed to fetch videos:", err);
      });
  }, [country, authChecked]);

  const handleRating = async (videoId, rating) => {
    try {
      await fetch("http://16.171.149.32.nip.io:4000/api/rate", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          videoId,
          rating
        })
      });
      alert("Оцінку збережено!");
    } catch (err) {
      console.error("Помилка збереження оцінки:", err);
      alert("Помилка збереження оцінки");
    }
  };

  const logout = () => {
    fetch("http://16.171.149.32.nip.io:4000/auth/logout", {
      method: "POST",
      credentials: "include"
    })
      .then(res => {
        if (!res.ok) throw new Error("Logout failed");
        setUser(null);
        navigate("/login");
      })
      .catch(err => {
        console.error("Logout error:", err);
        alert("Logout failed. Try again.");
      });
  };

  return (
    <div style={{ padding: 20 }}>
      <header style={{
        display: 'flex', justifyContent: 'space-between', alignItems: 'center',
        padding: '10px 20px', background: '#f2f2f2', borderBottom: '1px solid #ccc'
      }}>
        <div><strong>WorldMusic</strong></div>
        {user && (
          <div>
            <span style={{ marginRight: 10 }}>
              Welcome, {user.name}<br />
              <small>Last login: {new Date(user.lastLogin).toLocaleString()}</small>
            </span>
            <button onClick={logout}>Logout</button>
          </div>
        )}
      </header>

      <main style={{ textAlign: 'center', marginTop: 40 }}>
        <h1>Top Music Videos in {country}</h1>
        <select onChange={e => setCountry(e.target.value)} value={country}>
          <option value="US">USA</option>
          <option value="GB">UK</option>
          <option value="UA">Ukraine</option>
          <option value="JP">Japan</option>
          <option value="KR">South Korea</option>
          <option value="IN">India</option>
        </select>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {videos.map((video, index) => (
            <li key={index} style={{ marginTop: 30 }}>
              <h3>{video.title}</h3>
              <iframe
                width="320"
                height="180"
                src={`https://www.youtube.com/embed/${video.videoId}`}
                title={video.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>

              {/* Вивід середнього балу */}
              {ratings[video.videoId] !== null && (
                <div style={{ marginTop: 5 }}>
                  Середній бал: {ratings[video.videoId]}
                </div>
              )}

              <div style={{ marginTop: 10 }}>
                <label>Оцініть відео (1-10): </label>
                <input
                  type="number"
                  min="1"
                  max="10"
                  onChange={(e) => handleRating(video.videoId, parseInt(e.target.value))}
                />
              </div>
            </li>
          ))}
        </ul>
      </main>
    </div>
  );
};

export default Home;
